import React, { Component } from 'react';
// import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import {Container, Row, Col} from 'react-bootstrap';

class Tables extends Component {
    constructor(props) {
        super(props);
        this.state = {title: 'hello'}
    }

    componentDidMount() {
        this.getTables();
    }

    getTables() {
        fetch(`/api/table?id=${this.props.tableid}`).then(res => res.json()).then(data => this.setState({title: data.title, original: data.original, A: data.A, B: data.B, C: data.C}))
    }

    tableRows(table) {
        var pairs = this.state[table]
        return(Object.keys(pairs).map(key => {return(<Row><Col>{key}</Col><Col>{pairs[key].map(value => {return(<Row>{value}</Row>)})}</Col></Row>)}))
    }

    render() {
        this.tableRows('A')
        return(
            <Container>
                <Col>
                {this.tableRows('original')}
                </Col>
                <Col>
                </Col>
                <Col>
                </Col>
                <Col>
                </Col>
            </Container>
        );
    }
}

export default Tables;
