import React, { Component } from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Dropdown, Modal, Form } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import { IconButton } from "@material-ui/core";
import ShowModal from "./Modal";

const grid = 5;
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.state = this.getDefaultState();
  }

  getDefaultState() {
    let a = new Array(9).fill(0);
    let b = new Array(9).fill(0);
    let c = new Array(9).fill(0);
    let state_var = { A: a, B: b, C: c };
    return state_var;
  }
  getListStyle(isDraggingOver) {
    return {
      background: isDraggingOver ? "lightblue" : "lightgrey",
      padding: grid,
      minWidth: "250px",
      minHeight: "50px",
    };
  }
  getItemStyle(isDragging, draggableStyle) {
    return {
      // some basic styles to make the items look a bit nicer
      userSelect: "none",
      padding: grid * 2,
      margin: `0 0 ${grid}px 0`,

      // change background colour if dragging
      background: isDragging ? "lightgreen" : "grey",

      // styles we need to apply on draggables
      ...draggableStyle,
    };
  }
  tableRows(
    pairs,
    type,
    name = "draggable",
    editable = true,
    checkboxes = false,
    hypothesis_index
  ) {
    return Object.keys(pairs).map((key) => {
      var checkbox = key;
      if (checkboxes === true) {
        checkbox = (
          <Form>
            <Form.Check
              custom
              type={"checkbox"}
              defaultChecked={true}
              id={type + "_check_" + key}
              label={key}
              onChange={() => {
                this.props.handleRowChange(type, key, hypothesis_index);
              }}
            />
          </Form>
        );
      }
      return (
        <Container>
          <Row>
            <Col>
              <strong>{checkbox}</strong>
            </Col>
            <Col>
              <Droppable droppableId={type + "_" + key}>
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    style={this.getListStyle(snapshot.isDraggingOver)}
                    {...provided.droppableProps}
                  >
                    {pairs[key].map((value, id) => {
                      return (
                        <Draggable
                          draggableId={type + "_" + key + "_" + id}
                          index={id}
                        >
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              style={this.getItemStyle(
                                snapshot.isDragging,
                                provided.draggableProps.style
                              )}
                            >
                              <Row>
                                <input
                                  style={{
                                    margin: "5px",
                                    background: "transparent",
                                  }}
                                  name={name}
                                  id={"item_" + type + "_" + key + "_" + id}
                                  value={
                                    type === "original"
                                      ? value
                                      : Object.keys(value)[0]
                                  }
                                  onChange={(e) =>
                                    this.props.handleChange(
                                      e,
                                      "item_" + type + "_" + key + "_" + id
                                    )
                                  }
                                />
                              </Row>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                  </div>
                )}
              </Droppable>
            </Col>
          </Row>
        </Container>
      );
    });
  }

  hypothesesDisplay(hypo, label, type, idx) {
    var stylecolor =
      idx % 2
        ? { backgroundColor: "#E1ECF4" }
        : { backgroundColor: "light green" };
    var ShowModal = <></>;
    console.log(type, label, type, idx);
    if (type !== "original") {
      console.log("~~~~~~~~~~~~~~~");
      console.log(type);
      console.log(this.props[type + "_strategies"][idx]);
      ShowModal = (
        <ShowModal
          show={type == "original" ? false : this.state[type][idx]}
          onHide={() => this.setState(this.getDefaultState())}
          hypo={hypo}
          idx={idx}
          type={type}
          table={this.tableRows(
            this.props[type],
            type,
            "non-draggable",
            false,
            true
          )}
          strategies={this.props[type + "_strategies"][idx]}
          rows={this.props[type + "_rows"][idx]}
          handleStrategyChange={this.props.handleStrategyChange}
        />
      );
    }
    console.log(ShowModal);
    return (
      <Row style={stylecolor}>
        <IconButton
          onClick={() => {
            if (type == "original") return;
            var newState = this.getDefaultState();
            newState[type][idx] = 1;
            this.setState(newState);
          }}
        >
          <AddIcon />
        </IconButton>
        <Col
          class="input"
          id={"hypothesis_" + type + idx}
          type="textarea"
          contentEditable
        >
          {hypo}
        </Col>
        <select
          defaultValue={label}
          id={"label_" + type + idx}
          onChange={(e) => this.props.handleLabelChange(e, type, idx)}
        >
          <option value="E">E</option>
          <option value="N">N</option>
          <option value="C">C</option>
        </select>
      </Row>
    );
  }
  trash() {
    return (
      <Droppable droppableId={"trash"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver)}
            {...provided.droppableProps}
          >
            {() => {
              return <></>;
            }}
          </div>
        )}
      </Droppable>
    );
  }
  addCard() {
    console.log("ADD");
    return (
      <Droppable droppableId={"add"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver)}
            {...provided.droppableProps}
          >
            {[1].map((value) => {
              return (
                <Draggable draggableId={"new"}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={this.getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Row>
                        <input
                          style={{ margin: "5px", background: "transparent" }}
                          name="draggable"
                          id={"NEW"}
                          defaultValue={"ADD TEXT HERE"}
                        />
                      </Row>
                    </div>
                  )}
                </Draggable>
              );
            })}
          </div>
        )}
      </Droppable>
    );
  }
  showView(type) {
    // const ohy = Object.keys(this.props.oh).map((hypo, idx) => {
    //   return this.hypothesesDisplay(hypo, this.props.oh[hypo], "original", idx);
    // });
    console.log("**********");
    console.log(this.props.ah);
    const ahy = Object.keys(this.props.ah).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.ah[hypo], "A", idx);
    });
    const bhy = Object.keys(this.props.bh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.bh[hypo], "B", idx);
    });
    const chy = Object.keys(this.props.ch).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.ch[hypo], "C", idx);
    });
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={3}>{this.tableRows(this.props.A, "A")}</Col>
            <Col md={3}>{this.tableRows(this.props.B, "B")}</Col>
            <Col md={3}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={3}>{ohy}</Col>
            <Col md={3}>{ahy}</Col>
            <Col md={3}>{bhy}</Col>
            <Col md={3}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.A, "A")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{ahy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusB") {
      return (
        <>
          <Row>
            {" "}
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.B, "B")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{bhy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type == "trash") {
      return <Col md={6}>{this.trash()}</Col>;
    } else if (type == "add") {
      return <Col md={6}>{this.addCard()}</Col>;
    }
  }

  render() {
    return <>{this.showView(this.props.type)}</>;
  }
}

export default View;

