import React, { Component } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import {
  Container,
  Row,
  Col,
  Dropdown,
  Modal,
  Button,
  Form,
} from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import { IconButton } from "@material-ui/core";
class ShowModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hypo: "",
    };
  }
  render() {
    const modal_options = [
      "Change table to flip label",
      "Change hypothesis to flip label",
      "Add ‘true’ information from the table to confuse the model, or use text from other places in the table to",
      "Use the hypothesis as a prompt to write a new hypothesis",
      "Write a completely new hypothesis",
    ];
    return (
      <Modal
        show={this.props.show}
        onHide={this.props.onHide}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton></Modal.Header>

        <Modal.Body>
          <Container>
            <Col>
              <Row>{this.props.hypo}</Row>
              <Row>
                <Form>
                  {{ modal_options }.map((text, idx) => (
                    <Form.Check custom type="checkbox" label={text} />
                  ))}
                </Form>
              </Row>
            </Col>
            <Col></Col>
          </Container>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={this.props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default ShowModal;
