import React, { Component } from "react";
import { Container, Row, Col, Modal, Button, Form } from "react-bootstrap";
import { IconButton, TextareaAutosize } from "@material-ui/core";

class KeyModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      strategies: this.props.strategies,
    };
  }
  render() {
    return (
      <Modal
        show={this.props.show}
        onHide={() => this.props.onHide(this.state)}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        style={{
          webkitBorderRadius: "10px",
          mozBorderRadius: "10px",
          borderRadius: "10px",
        }}
      >
        <Modal.Header style={{ backgroundColor: "lightcoral" }} closeButton>
          <Modal.Title> Edit Key</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Row>
            <TextareaAutosize
              style={{
                marginLeft: "20px",
                background: "transparent",
                resize: "none",
                backgroundColor: "rgba(0, 0, 0, 0)",
              }}
              id={this.props.type + "_" + this.props.section_name}
              defaultValue={this.props.section_name}
              //   onChange={(e) =>
              //     this.props.handleChange(e, "item_" + type + "_" + key)
              //   }
            />
            <Button
              onClick={() => {
                console.log("this works");
                this.props.onEdit(
                  document.getElementById(
                    this.props.type + "_" + this.props.section_name
                  ).value
                );
                this.props.onHide();
              }}
            >
              Submit
            </Button>
          </Row>
        </Modal.Body>
        <Modal.Footer>
          <Button
            onClick={() => {
              this.props.onDelete(this.props.type, this.props.section_name);
              this.props.onHide();
            }}
            className="mr-auto"
          >
            Delete
          </Button>

          <Button
            onClick={() => {
              this.props.onHide();
            }}
          >
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default KeyModal;
