import React, { Component } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Dropdown, Modal } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import { IconButton } from "@material-ui/core";

const grid = 5;
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.state = this.getDefaultState();
  }

  getDefaultState() {
    let a = new Array(9).fill(0);
    let b = new Array(9).fill(0);
    let c = new Array(9).fill(0);
    let state_var = { A: a, B: b, C: c };
    return state_var;
  }
  getListStyle(isDraggingOver) {
    return {
      background: isDraggingOver ? "lightblue" : "lightgrey",
      padding: grid,
      minWidth: "250px",
      minHeight: "50px",
    };
  }
  getItemStyle(isDragging, draggableStyle) {
    return {
      // some basic styles to make the items look a bit nicer
      userSelect: "none",
      padding: grid * 2,
      margin: `0 0 ${grid}px 0`,

      // change background colour if dragging
      background: isDragging ? "lightgreen" : "grey",

      // styles we need to apply on draggables
      ...draggableStyle,
    };
  }
  tableRows(pairs, type, name = "draggable", editable = true) {
    return Object.keys(pairs).map((key) => {
      // console.log("WE ARE LOGGING NOW ~~~~~~~~~");
      // console.log(pairs[key]);
      return (
        <Container>
          <Row>
            <Col>
              <strong>{key}</strong>
            </Col>
            <Col>
              <Droppable droppableId={type + "_" + key}>
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    style={this.getListStyle(snapshot.isDraggingOver)}
                    {...provided.droppableProps}
                  >
                    {pairs[key].map((value, id) => {
                      // console.log("value is ^^^^^");
                      // console.log(value);
                      return (
                        <Draggable
                          draggableId={type + "_" + key + "_" + id}
                          index={id}
                        >
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              style={this.getItemStyle(
                                snapshot.isDragging,
                                provided.draggableProps.style
                              )}
                            >
                              <Row>
                                <input
                                  style={{
                                    margin: "5px",
                                    background: "transparent",
                                  }}
                                  name={name}
                                  id={"item_" + type + "_" + key + "_" + id}
                                  value={
                                    type === "original"
                                      ? value
                                      : Object.keys(value)[0]
                                  }
                                  onChange={(e) =>
                                    this.props.handleChange(
                                      e,
                                      "item_" + type + "_" + key + "_" + id
                                    )
                                  }
                                />
                              </Row>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                  </div>
                )}
              </Droppable>
            </Col>
          </Row>
        </Container>
      );
    });
  }
  MyVerticallyCenteredModal(props) {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <p>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta
            ac consectetur ac, vestibulum at eros.
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }

  App() {
    return (
      <>
        <IconButton onClick={() => setModalShow(true)}>
          <AddIcon />
        </IconButton>

        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </>
    );
  }

  hypothesesDisplay(hypo, label, type, idx, focus_type) {
    var stylecolor =
      idx % 2
        ? { backgroundColor: "#E1ECF4" }
        : { backgroundColor: "light green" };
    return (
      <Row style={stylecolor}>
        <Col
          class="input"
          id={"hypothesis_" + type + idx}
          type="textarea"
          contentEditable
        >
          {hypo}
        </Col>
        <select
          defaultValue={label}
          id={"label_" + type + idx}
          onChange={(e) => this.props.handleLabelChange(e, type, idx)}
        >
          <option value="E">E</option>
          <option value="N">N</option>
          <option value="C">C</option>
        </select>
      </Row>
    );
  }
  trash() {
    return (
      <Droppable droppableId={"trash"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver)}
            {...provided.droppableProps}
          >
            {() => {
              return <></>;
            }}
          </div>
        )}
      </Droppable>
    );
  }
  addCard() {
    console.log("ADD");
    return (
      <Droppable droppableId={"add"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver)}
            {...provided.droppableProps}
          >
            {[1].map((value) => {
              return (
                <Draggable draggableId={"new"}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={this.getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Row>
                        <input
                          style={{ margin: "5px", background: "transparent" }}
                          name="draggable"
                          id={"NEW"}
                          defaultValue={"ADD TEXT HERE"}
                        />
                      </Row>
                    </div>
                  )}
                </Draggable>
              );
            })}
          </div>
        )}
      </Droppable>
    );
  }
  showView(type) {
    const ohy = Object.keys(this.props.oh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.oh[hypo], "original", idx);
    });
    const ahy = Object.keys(this.props.ah).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.ah[hypo], "A", idx, type);
    });
    const bhy = Object.keys(this.props.bh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.bh[hypo], "B", idx, type);
    });
    const chy = Object.keys(this.props.ch).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.ch[hypo], "C", idx, type);
    });
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={3}>{this.tableRows(this.props.A, "A")}</Col>
            <Col md={3}>{this.tableRows(this.props.B, "B")}</Col>
            <Col md={3}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={3}>{ohy}</Col>
            <Col md={3}>{ahy}</Col>
            <Col md={3}>{bhy}</Col>
            <Col md={3}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.A, "A")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{ahy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusB") {
      return (
        <>
          <Row>
            {" "}
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.B, "B")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{bhy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type == "trash") {
      return <Col md={6}>{this.trash()}</Col>;
    } else if (type == "add") {
      return <Col md={6}>{this.addCard()}</Col>;
    }
  }

  render() {
    return <>{this.showView(this.props.type)}</>;
  }
}

export default View;
