import React, { Component } from "react";
import { Container, Row, Col, Modal, Button, Form } from "react-bootstrap";
class KeyModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      strategies: this.props.strategies,
    };
  }
  render() {
    return (
      <Modal
        show={this.props.show}
        onHide={() => this.props.onHide(this.state)}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        style={{
          webkitBorderRadius: "10px",
          mozBorderRadius: "10px",
          borderRadius: "10px",
        }}
      >
        <Modal.Header style={{ backgroundColor: "lightcoral" }} closeButton>
          <Modal.Title> Edit Key</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Row>
            <TextareaAutosize
              style={{
                marginLeft: "20px",
                background: "transparent",
                resize: "none",
                backgroundColor: "rgba(0, 0, 0, 0)",
                borderColor: "rgba(0, 0, 0, 0)",
              }}
              name={name}
              id={"item_" + type + "_" + key + "_" + id}
              value={section_name}
              onChange={(e) =>
                this.props.handleChange(e, "item_" + type + "_" + key)
              }
            />
          </Row>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={() => this.props.deleteKey(this.state)}>
            Delete
          </Button>

          <Button onClick={() => this.props.onHide(this.state)}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default KeyModal;
