import React, { Component } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import {Container, Row, Col} from 'react-bootstrap';

class Tables extends Component {
    constructor(props) {
        super(props);
        this.state = {title: '', original: '', A: '', B: '', C: ''}
    }

    componentDidMount() {
        this.getTables();
    }

    getTables() {
        console.log('called')
        fetch(`/api/table?id=${this.props.tableid}`).then(res => res.json()).then(data => {this.setState({title: data.title, 'original': data.original, 'A': data['A'], 'B': data.B, 'C': data.C})})
    }

    tableRows(pairs, type) {
        Object.defineProperty(String.prototype, 'hashCode', {
            value: function() {
            var hash = 0, i, chr;
            for (i = 0; i < this.length; i++) {
            chr = this.charCodeAt(i);
            hash = ((hash << 5) - hash) + chr;
            hash |= 0; // Convert to 32bit integer
            }
            return hash;
            }
            }); 
        return(Object.keys(pairs).map(key => {return(<Container><Row><Col><strong>{key}</strong></Col><Col>
        <Droppable droppableId={type + '_' + key}>
            {(provided, snapshot) => (
                <div ref={provided.innerRef}>
                    {pairs[key].map((value, id) => {
                        return(
                            <Draggable draggableId={(type + '_' + key + id).hashCode()}>
                                {(provided, snapshot) => (
                                    <div ref={provided.innerRef}><Row>{value}</Row></div>
                                )}
                            </Draggable>
                        )
                    })}
                </div>
            )}
        </Droppable>
        </Col></Row></Container>)}))
    }

    reorder(list, startIndex, endIndex) {
        const result = Array.from(list);
        const [removed] = result.splice(startIndex, 1);
        result.splice(endIndex, 0, removed);

        return result;
      };

      /**
       * Moves an item from one list to another list.
       */
    move(source, destination, droppableSource, droppableDestination) {
        const sourceClone = Array.from(source);
        const destClone = Array.from(destination);
        const [removed] = sourceClone.splice(droppableSource.index, 1);

        destClone.splice(droppableDestination.index, 0, removed);

        const result = {};
        result[droppableSource.droppableId] = sourceClone;
        result[droppableDestination.droppableId] = destClone;

        return result;
      };

    onDragEnd(result) {
        const { source, destination } = result;

        // dropped outside the list
        if (!destination) {
          return;
        }
        const sInd = +source.droppableId;
        const dInd = +destination.droppableId;

        if (sInd === dInd) {
          const items = this.reorder(this.state[sInd], source.index, destination.index);
          const newState = [...this.state];
          newState[sInd] = items;
          this.setState(newState);
        } else {
          const result = this.move(this.state[sInd], this.state[dInd], source, destination);
          const newState = [...this.state];
          newState[sInd] = result[sInd];
          newState[dInd] = result[dInd];

          this.setState(newState.filter(group => group.length));
        }
      }

    render() {
        return(
            <Container >
                <DragDropContext onDragEnd={this.onDragEnd}>
                <Row className="justify-content-center">
                    <h1>{this.state.title}</h1>
                </Row>
                <Row>
                <Col md={3}>
                {this.tableRows(this.state.original, 'original')}
                </Col>
                <Col md={3}>
                {this.tableRows(this.state.A, 'A')}
                </Col>
                <Col md={3}>
                {this.tableRows(this.state.B, 'B')}
                </Col>
                <Col md={3}>
                {this.tableRows(this.state.C, 'C')}
                </Col>
                </Row>
                </DragDropContext>
            </Container>
        );
    }
}

export default Tables;
