const bgcolors_dict = {
  original: "#ca76f5",
  A: "#5ca1db",
  B: "#56d3e3",
  C: "#ffae7d",
  trash: "#ccc5b4",
  add: "#ccc5b4",
};

export function getListStyle(isDraggingOver, type) {
  const bgcolor = bgcolors_dict[type];
  return {
    background: isDraggingOver ? "#f5eef8" : bgcolor,
    padding: grid,
    minWidth: "250px",
    minHeight: "50px",
  };
}
