import React, { Component } from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import { getListStyle, getItemStyle } from "./Colours";
import { IconButton, TextareaAutosize } from "@material-ui/core";
import HypothesisModal from "./HypothesisModal";
import KeyModal from "./KeyModal";
import Hypothesis from "./Hypothesis";
import Table from "./Table";

// for dark mode
const bgcolors_dict = {
  original: "#DEB2F4",
  A: "#A4CDF0",
  B: "#A2DCE2",
  C: "#F9D3BC",
  trash: "#ccc5b4",
  add: "#ccc5b4",
};

// for light mode
// const bgcolors_dict = {
//   original: "#ca76f5",
//   A: "#5ca1db",
//   B: "#56d3e3",
//   C: "#ffae7d",
//   trash: "#ccc5b4",
//   add: "#ccc5b4",
// };
const hypotheses_dark_colour = "#92d5e6";
const hypotheses_light_colour = "#e1edf0";
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.getDefaultState = this.getDefaultState.bind(this);
    this.handleStateChange = this.handleStateChange.bind(this);
    this.state = this.getDefaultState();
  }

  getDefaultState() {
    let a = new Array(9).fill(0);
    let b = new Array(9).fill(0);
    let c = new Array(9).fill(0);

    let a_rows = new Array(Object.keys(this.props.A).length).fill(0);
    let b_rows = new Array(Object.keys(this.props.B).length).fill(0);
    let c_rows = new Array(Object.keys(this.props.C).length).fill(0);

    let state_var = {
      A: a,
      B: b,
      C: c,
      A_keys: a_rows,
      B_keys: b_rows,
      C_keys: c_rows,
    };
    return state_var;
  }

  tableRows(
    pairs,
    type,
    name = "draggable",
    editable = true,
    checkboxes = false,
    hypotheses
  ) {
    return (
      <Table
        pairs={pairs}
        type={type}
        name={name}
        editable={editable}
        checkboxes={checkboxes}
        hypotheses={hypotheses}
        hypo_info={this.props[type.toLowerCase() + "h"]}
