    return (
      <Hypothesis
        hypo={hypo}
        hypo_info={hypo_info}
        type={type}
        idx={idx}
        showValue={type == "original" ? false : this.state[type][idx]}
        getDefaultState={() => this.getDefaultState()}
        handleHypothesisChange={(e, type, hypo) =>
          this.props.handleHypothesisChange(e, type, hypo)
