              </Row>
              {this.tableRows(this.props.B, "B")}
            </Col>
            <Col md={3}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Original Hypotheses</h5>
              </Row>
              {ohy}
            </Col>
            <Col md={3}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table B Hypotheses</h5>
              </Row>
              {bhy}
            </Col>
          </Row>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Row className="mt-3">
            <Col md={3}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Original Table</h5>
              </Row>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
