import React, { Component } from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Form } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import { IconButton, TextareaAutosize } from "@material-ui/core";
import ShowModal from "./Modal";

const grid = 5;
const bgcolors_dict = {
  original: "#92d5e6",
  A: "#12d5e6",
  B: "#22d5e6",
  C: "#32d5e6",
  trash: "#42d5e6",
  add: "#52d5e6",
};
const rowcolor = "#f5eef8";
const hypotheses_dark_colour = "lightgreen";
const hypotheses_light_colour = "#E1ECF4";
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.state = this.getDefaultState();
  }

  getDefaultState() {
    let a = new Array(9).fill(0);
    let b = new Array(9).fill(0);
    let c = new Array(9).fill(0);
    let state_var = { A: a, B: b, C: c };
    return state_var;
  }
  getListStyle(isDraggingOver, type) {
    const bgcolor = bgcolors_dict[type];
    return {
      background: isDraggingOver ? "#BADDE0" : bgcolor,
      padding: grid,
      minWidth: "250px",
      minHeight: "50px",
    };
  }
  getItemStyle(isDragging, draggableStyle) {
    return {
      // some basic styles to make the items look a bit nicer
      userSelect: "none",
      padding: grid * 2,
      margin: `0 0 ${grid}px 0`,

      // change background colour if dragging
      background: isDragging ? "lightgreen" : rowcolor,

      // styles we need to apply on draggables
      ...draggableStyle,
    };
  }
  tableRows(
    pairs,
    type,
    name = "draggable",
    editable = true,
    checkboxes = false,
    hypo
  ) {
    var rows = Object.keys(pairs).map((key) => {
      var checkbox = key;
      // console.log("$$$$$$$$$$$$$$");
      // console.log(this.props);
      // console.log(type, hypo);
      if (checkboxes === true) {
        checkbox = (
          <Form>
            <Form.Check
              custom
              type={"checkbox"}
              defaultChecked={
                this.props[type.toLowerCase() + "h"][hypo][2].includes(key)
                  ? true
                  : false
              }
              id={type + "_check_" + key}
              label={key}
              className="checkbox label"
              onChange={() => {
                this.props.handleRowChange(type, key, hypo);
              }}
            />
          </Form>
        );
      }

      return (
        <Container>
          <Droppable droppableId={type + "_" + key}>
            {(provided, snapshot) => (
              <div
                ref={provided.innerRef}
                style={this.getListStyle(snapshot.isDraggingOver, type)}
                {...provided.droppableProps}
              >
                {!checkboxes && (
                  <center
                    style={{
                      backgroundColor: bgcolors_dict[type],
                    }}
                  >
                    <strong>{checkbox}</strong>
                  </center>
                )}
                {checkboxes && (
                  <div
                    style={{
                      backgroundColor: bgcolors_dict,
                      marginLeft: "20px",
                    }}
                  >
                    <strong>{checkbox}</strong>
                  </div>
                )}
                {pairs[key].map((value, id) => {
                  // console.log("value is ^^^^^");
                  // console.log(value);
                  return (
                    <Draggable
                      draggableId={type + "_" + key + "_" + id}
                      index={id}
                      className="draggable"
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          style={this.getItemStyle(
                            snapshot.isDragging,
                            provided.draggableProps.style
                          )}
                        >
                          <Row>
                            <TextareaAutosize
                              style={{
                                marginLeft: "20px",
                                background: "transparent",
                                resize: "none",
                                backgroundColor: "rgba(0, 0, 0, 0)",
                                borderColor: "rgba(0, 0, 0, 0)",
                              }}
                              name={name}
                              id={"item_" + type + "_" + key + "_" + id}
                              value={
                                type === "original"
                                  ? value
                                  : Object.keys(value)[0]
                              }
                              onChange={(e) =>
                                this.props.handleChange(
                                  e,
                                  "item_" + type + "_" + key + "_" + id
                                )
                              }
                            />
                          </Row>
                        </div>
                      )}
                    </Draggable>
                  );
                })}
              </div>
            )}
          </Droppable>
          <Row>
            <IconButton>
              <AddIcon />
            </IconButton>
          </Row>
        </Container>
      );
    });
    if (type !== "original")
      rows =
        rows +
        (
          <Container>
            {" "}
            <Row>
              <IconButton>
                <AddIcon />
              </IconButton>
            </Row>
          </Container>
        );

    console.log(rows);

    return rows;
  }

  hypothesesDisplay(hypo, hypo_info, type, idx) {
    var stylecolor =
      idx % 2
        ? { backgroundColor: hypotheses_light_colour }
        : { backgroundColor: hypotheses_dark_colour };

    var showModal = <></>;
    // console.log(type, hypo_info, type, idx);
    if (type !== "original") {
      // console.log("~~~~~~~~~~~~~~~");
      // console.log(this.props[type.toLowerCase() + "h"][hypo]);
      // console.log(hypo);
      // console.log("show modal is ");
      // console.log(type == "original" ? false : this.state[type][idx]);
      showModal = (
        <>
          <IconButton
            onClick={() => {
              if (type == "original") return;
              var newState = this.getDefaultState();
              newState[type][idx] = 1;
              this.setState(newState);
              // console.log("^^^^^^^^^we changed the state!");
              // console.log(newState[type][idx]);
            }}
          >
            <AddIcon />
          </IconButton>
          <ShowModal
            show={type == "original" ? false : this.state[type][idx]}
            onHide={() => this.setState(this.getDefaultState())}
            hypo={hypo}
            idx={idx}
            type={type}
            table={this.tableRows(
              this.props[type],
              type,
              "non-draggable",
              false,
              true,
              hypo
            )}
            strategies={this.props[type.toLowerCase() + "h"][hypo][1]}
            rows={this.props[type.toLowerCase() + "h"][hypo][2]}
            handleStrategyChange={this.props.handleStrategyChange}
          />
        </>
      );
    }
    return (
      <Row className="align-items-center" style={stylecolor}>
        <Col style={{ height: "100%s" }}>
          <div className="float-right" style={{ width: "20px" }}>
            <Row>
              {type == "original" && (
                <select defaultValue={hypo_info} id={"label_" + type + idx}>
                  <option value={hypo_info}>{hypo_info}</option>
                </select>
              )}
              {type !== "original" && (
                <select
                  defaultValue={this.props[type.toLowerCase() + "h"][hypo][0]}
                  id={"label_" + type + idx}
                  onChange={(e) => this.props.handleLabelChange(e, type, idx)}
                >
                  <option value="E">E</option>
                  <option value="N">N</option>
                  <option value="C">C</option>
                </select>
              )}
            </Row>
            <Row>{showModal}</Row>
          </div>
        </Col>
        <Col>
          <div style={{ width: "230px", overflow: "hidden" }}>
            <TextareaAutosize
              id={"hypothesis_" + type + idx}
              type="textarea"
              defaultValue={hypo}
              style={{
                resize: "none",
                backgroundColor: "rgba(0, 0, 0, 0)",
                borderColor: "rgba(0, 0, 0, 0)",
              }}
              onChange={(e) => this.props.handleHypothesisChange(e, type, hypo)}
              contentEditable
            />
          </div>
        </Col>
      </Row>
    );
  }
  trash() {
    return (
      <Droppable droppableId={"trash"} style={{ height: "40px" }}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver, "trash")}
            {...provided.droppableProps}
          >
            <div
              style={{
                marginTop: "15px",
                marginBottom: "15px",
                marginLeft: "20px",
              }}
            >
              Drag and drop a value here to delete it
            </div>
            {() => {
              return <></>;
            }}
          </div>
        )}
      </Droppable>
    );
  }
  addCard() {
    // console.log("ADD");
    return (
      <Droppable droppableId={"add"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver, "add")}
            {...provided.droppableProps}
          >
            {[1].map((value) => {
              return (
                <Draggable draggableId={"new"}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={this.getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Row>
                        <input
                          style={{
                            marginLeft: "20px",
                            background: "transparent",
                            resize: "none",
                            backgroundColor: "rgba(0, 0, 0, 0)",
                            borderColor: "rgba(0, 0, 0, 0)",
                            width: "100%",
                          }}
                          name="draggable"
                          id={"NEW"}
                          defaultValue={
                            "Add a new value here! (write text, then drag and drop)"
                          }
                        />
                      </Row>
                    </div>
                  )}
                </Draggable>
              );
            })}
          </div>
        )}
      </Droppable>
    );
  }
  showView(type) {
    const ohy = Object.keys(this.props.oh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.oh[hypo], "original", idx);
    });
    var ah_items = Object.keys(this.props.ah).map((key) => [
      key,
      this.props.ah[key],
    ]);
    ah_items.sort((first, second) => second[1][3] - first[1][3]);
    // console.log(ah_items);
    const ahy = ah_items.map((hypo, idx) => {
      // console.log(hypo);
      return this.hypothesesDisplay(hypo[0], hypo[1], "A", idx);
    });
    var bh_items = Object.keys(this.props.bh).map((key) => [
      key,
      this.props.bh[key],
    ]);
    bh_items.sort((first, second) => second[1][3] - first[1][3]);
    const bhy = bh_items.map((hypo, idx) => {
      return this.hypothesesDisplay(hypo[0], hypo[1], "B", idx);
    });
    var ch_items = Object.keys(this.props.ch).map((key) => [
      key,
      this.props.ch[key],
    ]);
    ch_items.sort((first, second) => second[1][3] - first[1][3]);
    const chy = ch_items.map((hypo, idx) => {
      return this.hypothesesDisplay(hypo[0], hypo[1], "C", idx);
    });
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Original Table</h5>
              </Row>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table A</h5>
              </Row>
              {this.tableRows(this.props.A, "A")}
            </Col>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table B</h5>
              </Row>
              {this.tableRows(this.props.B, "B")}
            </Col>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table C</h5>
              </Row>
              {this.tableRows(this.props.C, "C")}
            </Col>
          </Row>
          <Row>
            <Col md={3} style={{ width: "100%", paddingRight: "10px" }}>
              {ohy}
            </Col>
            <Col md={3} style={{ width: "100%" }}>
              {ahy}
            </Col>
            <Col md={3} style={{ width: "100%" }}>
              {bhy}
            </Col>
            <Col md={3} style={{ width: "100%" }}>
              {chy}
            </Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.A, "A")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{ahy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusB") {
      return (
        <>
          <Row>
            {" "}
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.B, "B")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{bhy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type == "trash") {
      return <Col md={6}>{this.trash()}</Col>;
    } else if (type == "add") {
      return <Col md={6}>{this.addCard()}</Col>;
    }
  }

  render() {
    return <>{this.showView(this.props.type)}</>;
  }
}

export default View;
