import React, { Component } from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";

import { IconButton, TextareaAutosize } from "@material-ui/core";

function AddValue(props) {
  return (
    <Droppable droppableId={"add"}>
      {(provided, snapshot) => (
        <div
          ref={provided.innerRef}
          style={this.getListStyle(snapshot.isDraggingOver, "add")}
          {...provided.droppableProps}
        >
          {[1].map((value) => {
            return (
              <Draggable draggableId={"new"}>
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={this.getItemStyle(
                      snapshot.isDragging,
                      provided.draggableProps.style
                    )}
                  >
                    <Row>
                      <input
                        style={{
                          marginLeft: "20px",
                          background: "transparent",
                          resize: "none",
                          backgroundColor: "rgba(0, 0, 0, 0)",
                          borderColor: "rgba(0, 0, 0, 0)",
                          width: "100%",
                        }}
                        name="draggable"
                        id={"NEW"}
                        defaultValue={
                          "Add a new value here! (write text, then drag and drop)"
                        }
                      />
                    </Row>
                  </div>
                )}
              </Draggable>
            );
          })}
        </div>
      )}
    </Droppable>
  );
}

export default AddValue;
