import React, { Component } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Button } from "react-bootstrap";

const grid = 5;
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }
  getListStyle(isDraggingOver) {
    return {
      background: isDraggingOver ? "lightblue" : "lightgrey",
      padding: grid,
      width: 250,
      minHeight: "50px",
    };
  }
  getItemStyle(isDragging, draggableStyle) {
    return {
      // some basic styles to make the items look a bit nicer
      userSelect: "none",
      padding: grid * 2,
      margin: `0 0 ${grid}px 0`,

      // change background colour if dragging
      background: isDragging ? "lightgreen" : "grey",

      // styles we need to apply on draggables
      ...draggableStyle,
    };
  }
  tableRows(pairs, type) {
    return Object.keys(pairs).map((key) => {
      return (
        <Container>
          <Row>
            <Col>
              <strong>{key}</strong>
            </Col>
            <Col>
              <Droppable droppableId={type + "_" + key}>
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    style={this.getListStyle(snapshot.isDraggingOver)}
                    {...provided.droppableProps}
                  >
                    {pairs[key].map((value, id) => {
                      return (
                        <Draggable
                          draggableId={type + "_" + key + "_" + id}
                          index={id}
                        >
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              style={this.getItemStyle(
                                snapshot.isDragging,
                                provided.draggableProps.style
                              )}
                            >
                              <Row>
                                <div
                                  style={{ margin: "5px" }}
                                  class="input"
                                  name="draggable"
                                  id={"item_" + type + "_" + key + "_" + id}
                                  type="textbox"
                                  contentEditable
                                >
                                  {value}
                                </div>
                              </Row>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                  </div>
                )}
              </Droppable>
            </Col>
          </Row>
        </Container>
      );
    });
  }
  handleInputChange(event) {
    console.log(event);
  }
  hypothesesDisplay(hypo, type, idx) {
    return (
      <div
        class="input"
        id={"hypothesis_" + type + idx}
        type="textbox"
        onChange={this.props.handleInputChange}
        contentEditable
      >
        {hypo}
      </div>
    );
  }
  showView(type) {
    console.log(this.props.oh);
    const ohy = Object.keys(this.props.oh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, "original", idx);
    });
    const ahy = Object.keys(this.props.ah).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, "A", idx);
    });
    const bhy = Object.keys(this.props.bh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, "B", idx);
    });
    const chy = Object.keys(this.props.ch).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, "C", idx);
    });
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3}>{this.tableRows(this.props.original, "original")}</Col>
            <Col md={3}>{this.tableRows(this.props.A, "A")}</Col>
            <Col md={3}>{this.tableRows(this.props.B, "B")}</Col>
            <Col md={3}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={3}>{ohy}</Col>
            <Col md={3}>{ahy}</Col>
            <Col md={3}>{bhy}</Col>
            <Col md={3}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Row>
            <Col md={6}>{this.tableRows(this.props.original, "original")}</Col>
            <Col md={6}>{this.tableRows(this.props.A, "A")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{ahy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusB") {
      return (
        <>
          <Row>
            {" "}
            <Col md={6}>{this.tableRows(this.props.original, "original")}</Col>
            <Col md={6}>{this.tableRows(this.props.B, "B")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Row>
            <Col md={6}>{this.tableRows(this.props.original, "original")}</Col>
            <Col md={6}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{chy}</Col>
          </Row>
        </>
      );
    }
  }

  render() {
    return <>{this.showView(this.props.type)}</>;
  }
}

export default View;
