import React, { Component } from "react";
import {
  Container,
  Row,
  Col,
  Dropdown,
  Modal,
  Button,
  Form,
} from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import { IconButton } from "@material-ui/core";
class ShowModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      strategies: this.props.strategies,
    };
  }
  render() {
    const modal_options = [
      "Change table to flip label",
      "Change hypothesis to flip label",
      "Add ‘true’ information from the table to confuse the model, or use text from other places in the table to",
      "Use the hypothesis as a prompt to write a new hypothesis",
      "Write a completely new hypothesis",
      "Other",
    ];
    return (
      <Modal
        show={this.props.show}
        onHide={() => this.props.onHide(this.state)}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title> {this.props.hypo}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Container fliud>
            <Col md={3}>
              <Row>
                <Form>
                  {modal_options.map((text, idx) => (
                    <Form.Check
                      type="checkbox"
                      label={text}
                      defaultChecked={
                        this.props.strategies.charAt(idx) === "1" ? true : false
                      }
                      onChange={(e) => {
                        this.props.handleStrategyChange(
                          this.props.type,
                          this.props.idx,
                          idx
                        );
                      }}
                    />
                  ))}
                </Form>
              </Row>
            </Col>
            <Col md={3}>{this.props.table}</Col>
          </Container>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={() => this.props.onHide(this.state)}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default ShowModal;
