import React, { Component } from 'react';
// import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import {Container, Row, Col} from 'react-bootstrap';

class Tables extends Component {
    constructor(props) {
        super(props);
        this.state = {title: '', original: '', A: '', B: '', C: ''}

    }

    componentDidMount() {
        this.getTables();
    }

    getTables() {
        console.log('called')
        fetch(`/api/table?id=${this.props.tableid}`).then(res => res.json()).then(data => {this.setState({title: data.title, 'original': data.original, 'A': data['A'], 'B': data.B, 'C': data.C})})
    }

    tableRows(pairs) {
        console.log(pairs)
        console.log(Object.keys(pairs))
        return(Object.keys(pairs).map(key => {return(<Container><Row><Col md={1}>{key}</Col><Col md={2}>{pairs[key].map(value => {return(<Row>{value}</Row>)})}</Col></Row></Container>)}))
    }

    render() {
        return(
            <Container>
                <Col md={3}>
                {this.tableRows(this.state.original)}
                </Col>
                <Col>
                {this.tableRows(this.state.A)}
                </Col>
                <Col>
                {this.tableRows(this.state.B)}
                </Col>
                <Col>
                {this.tableRows(this.state.C)}
                </Col>
            </Container>
        );
    }
}

export default Tables;
