import React, { Component } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Button } from "react-bootstrap";
import View from "./View";

const axios = require("axios");

class Tables extends Component {
  constructor(props) {
    super(props);
    this.onDragEnd = this.onDragEnd.bind(this);
    this.submit = this.submit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleLabelChange = this.handleLabelChange.bind(this);
    this.state = {
      title: "",
      original: "",
      A: "",
      B: "",
      C: "",
      view: "drag",
      oh: "",
      ah: "",
      bh: "",
      ch: "",
      cats: "",
    };
  }

  componentDidMount() {
    this.getTables();
  }

  handleChange(e, id) {
    console.log(e.target.value);
    console.log(id);
    var table = id.split("_")[1];
    var key = id.split("_")[2];
    var index = id.split("_")[3];
    const newState = JSON.parse(JSON.stringify(this.state));
    old_dict = newState[table][key][index];
    old_meta = Object.values(old_dict);
    new_meta = this.setCharAt(old_meta, 5, "1");
    new_dict = {};
    new_dict[e.target.value] = new_meta;
    console.log(new_dict);
    newState[table][key][index] = new_dict;
    this.setState(newState);
    console.log(newState);
  }

  getTables() {
    console.log(this.props);
    const table_id = this.props.match.params.id;
    console.log("table_id is **************" + table_id);
    fetch(`/api/table/?id=${table_id}`)
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          title: data.title,
          original: data.original,
          A: data.A,
          B: data.B,
          C: data.C,
          oh: data.oh,
          ah: data.ah,
          bh: data.bh,
          ch: data.ch,
        });
        console.log(this.state);
      });
    fetch(`/api/cats`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Cats");
        console.log(data);
        this.setState({
          cats: data,
        });
      });
  }

  submit() {
    console.log(this.state);
    var title = this.state.title;
    var A = this.state.A;
    var B = this.state.B;
    var C = this.state.C;
    A["title"] = title;
    B["title"] = title;
    C["title"] = title;
    var id = this.props.tableid;
    var ah = this.state.ah;
    var bh = this.state.bh;
    var ch = this.state.ch;

    axios
      .post(`/api/save`, {
        title: title,
        A: A,
        B: B,
        C: C,
        id: id,
        ah: ah,
        bh: bh,
        ch: ch,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        //console.log(error);
      });
  }

  // helper function to change character at index of a string to chr
  setCharAt(str, index, chr) {
    if (index > str.length - 1) return str;
    return str.substring(0, index) + chr + str.substring(index + 1);
  }

  reorder(list, startIndex, endIndex) {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
  }

  move(source, destination, droppableSource, droppableDestination) {
    const sourceClone = Array.from(source);
    const destClone = Array.from(destination);
    const [removed] = sourceClone.splice(droppableSource.index, 1);
    for (var key in removed) removed[key] = this.setCharAt(removed[key], 4, 1);

    destClone.splice(droppableDestination.index, 0, removed);

    const result = {};
    result[droppableSource.droppableId] = sourceClone;
    result[droppableDestination.droppableId] = destClone;
    console.log("removed is");
    console.log(removed);
    return result;
  }
  copy(source, destination, droppableSource, droppableDestination) {
    const sourceClone = Array.from(source);
    const destClone = Array.from(destination);
    const [removed] = sourceClone.splice(droppableSource.index, 1);
    var newDict = {};
    newDict[removed] = "000010";
    destClone.splice(droppableDestination.index, 0, newDict);
    return destClone;
  }

  remove(source, droppableSource) {
    const sourceClone = Array.from(source);
    sourceClone.splice(droppableSource.index, 1);
    return sourceClone;
  }
  add(destination, droppableDestination) {
    const destClone = Array.from(destination);
    destClone.splice(droppableDestination.index, 0, "ADD TEXT HERE");
    return destClone;
  }

  onDragEnd(result) {
    const { source, destination } = result;

    // dropped outside the list
    if (!destination) {
      return;
    }
    // droppableIds are of the form <table_name>_<key> | trash | add
    const sInd = source.droppableId;
    const dInd = destination.droppableId;
    console.log("sInd " + sInd);
    console.log("dInd " + dInd);
    console.log(JSON.parse(JSON.stringify(this.state)));
    // dropped in original table or the add card space
    if (dInd === "add" || dInd.split("_")[0] === "original") {
      return;
    }
    // if put into trash, delete
    else if (dInd === "trash") {
      const newSource = this.remove(
        this.state[sInd.split("_")[0]][sInd.split("_")[1]],
        source
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[sInd.split("_")[0]][sInd.split("_")[1]] = newSource;
      this.setState(newState);
    }
    // if not the same key-category and not the same field name, drop is not allowed
    else if (
      sInd.split("_")[1] !== dInd.split("_")[1] &&
      this.state.cats[sInd.split("_")[1]] !==
        this.state.cats[dInd.split("_")[1]]
    ) {
      return;
    }
    // if pulled from original table, copy
    else if (sInd.split("_")[0] === "original") {
      const newDestination = this.copy(
        this.state[sInd.split("_")[0]][sInd.split("_")[1]],
        this.state[dInd.split("_")[0]][dInd.split("_")[1]],
        source,
        destination
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[dInd.split("_")[0]][dInd.split("_")[1]] = newDestination;
      this.setState(newState);
      console.log(newState);
    }
    // if pulled from add space, add new card
    else if (sInd === "add") {
      const newDestination = this.add(
        this.state[dInd.split("_")[0]][dInd.split("_")[1]],
        destination
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[dInd.split("_")[0]][dInd.split("_")[1]] = newDestination;
      this.setState(newState);
    }
    // if dropped in the same place, we reorder the items
    else if (sInd === dInd) {
      const items = this.reorder(
        this.state[sInd.split("_")[0]][sInd.split("_")[1]],
        source.index,
        destination.index
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[sInd.split("_")[0]][sInd.split("_")[1]] = items;
      this.setState(newState);
    }
    // if dropped in a different place, move
    else {
      const result = this.move(
        this.state[sInd.split("_")[0]][sInd.split("_")[1]],
        this.state[dInd.split("_")[0]][dInd.split("_")[1]],
        source,
        destination
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[sInd.split("_")[0]][sInd.split("_")[1]] = result[sInd];
      newState[dInd.split("_")[0]][dInd.split("_")[1]] = result[dInd];

      this.setState(newState);
    }
  }

  handleLabelChange(e, type, idx) {
    console.log("event*********");
    console.log(e.target.value);
    const new_label = e.target.value;
    const state_name = type.toLowerCase() + "h";
    const hypothesis = document.getElementById("hypothesis_" + type + idx)
      .innerText;
    const newState = JSON.parse(JSON.stringify(this.state));
    newState[state_name][hypothesis] = new_label;
    this.setState(newState);
    console.log(newState[state_name]);
  }
  render() {
    console.log("We have re-rendered:");
    return (
      <Container>
        <DragDropContext onDragEnd={this.onDragEnd}>
          <Row className="justify-content-center">
            <Button
              onClick={() => {
                this.setState({ view: "drag" });
              }}
            >
              Drag and Drop
            </Button>
            <Button
              onClick={() => {
                this.setState({ view: "focusA" });
              }}
            >
              Focus A
            </Button>
            <Button onClick={() => this.setState({ view: "focusB" })}>
              Focus B
            </Button>
            <Button onClick={() => this.setState({ view: "focusC" })}>
              Focus C
            </Button>
            <h1>{this.state.title}</h1>
          </Row>
          <Row>
            <View
              type={"add"}
              original={this.state.original}
              A={this.state.A}
              B={this.state.B}
              C={this.state.C}
              oh={this.state.oh}
              ah={this.state.ah}
              bh={this.state.bh}
              ch={this.state.ch}
            />
            <View
              type={"trash"}
              original={this.state.original}
              A={this.state.A}
              B={this.state.B}
              C={this.state.C}
              oh={this.state.oh}
              ah={this.state.ah}
              bh={this.state.bh}
              ch={this.state.ch}
            />
          </Row>
          <Row show={this.state.view === "drag"}>
            <View
              type={this.state.view}
              original={this.state.original}
              A={this.state.A}
              B={this.state.B}
              C={this.state.C}
              oh={this.state.oh}
              ah={this.state.ah}
              bh={this.state.bh}
              ch={this.state.ch}
              handleChange={this.handleChange}
              handleLabelChange={this.handleLabelChange}
            />
          </Row>
        </DragDropContext>
        {/* <Button onClick={this.addEventListeners}>test</Button> */}
        <Button onClick={this.submit}>Submit</Button>
      </Container>
    );
  }
}

