import React, { Component } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Button } from "react-bootstrap";
import View from "./View";

const axios = require("axios");

class Tables extends Component {
  constructor(props) {
    super(props);
    this.onDragEnd = this.onDragEnd.bind(this);
    this.submit = this.submit.bind(this);
    this.addEventListeners = this.addEventListeners.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.state = {
      title: "",
      original: "",
      A: "",
      B: "",
      C: "",
      view: "drag",
      oh: "",
      ah: "",
      bh: "",
      ch: "",
      cats: "",
    };
  }

  componentDidMount() {
    this.getTables();
    // document.addEventListener("DOMContentLoaded", () => {
    //   console.log("dom content loaded");
    //   this.addEventListeners();
    // });
  }

  addEventListeners() {
    var draggables = document.getElementsByName("draggable");

    draggables.forEach(function (draggable) {
      console.log(draggable);
      draggable.addEventListener("input", (event) => {
        console.log(draggable["id"]);
        console.log(event);
        var id = draggable["id"];
        this.handleInputChange(
          id.split("_")[0],
          id.split("_")[1],
          id.split("_")[2],
          draggable.innerText
        );
      });
    });
  }

  handleInputChange(table, key, index, new_text) {
    const newState = JSON.parse(JSON.stringify(this.state));
    newState[table][key][index] = new_text;
    this.setState(newState);
    console.log(this.state[table][key][index]);
  }

  getTables() {
    fetch(`/api/table?id=${this.props.tableid}`)
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          title: data.title,
          original: data.original,
          A: data.A,
          B: data.B,
          C: data.C,
          oh: data.oh,
          ah: data.ah,
          bh: data.bh,
          ch: data.ch,
        });
      });
    fetch(`/api/cats/movie`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        this.setState({
          cats: data,
        });
      });
  }

  submit() {
    console.log(this.state);
    var title = this.state.title;
    var A = this.state.A;
    var B = this.state.B;
    var C = this.state.C;
    A["title"] = title;
    B["title"] = title;
    C["title"] = title;
    var id = this.props.tableid;
    var ah = {};
    var bh = {};
    var ch = {};
    for (var i = "0"; i < "9"; i++) {
      ah[document.getElementById("hypothesis_A" + i).innerText] = "E";
      bh[document.getElementById("hypothesis_B" + i).innerText] = "N";
      ch[document.getElementById("hypothesis_C" + i).innerText] = "C";
    }
    console.log(ah);
    console.log(this.state);

    axios
      .post(`/api/save`, {
        title: title,
        A: A,
        B: B,
        C: C,
        id: id,
        ah: ah,
        bh: bh,
        ch: ch,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        //console.log(error);
      });
  }

  reorder(list, startIndex, endIndex) {
    console.log(list);
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
  }

  move(source, destination, droppableSource, droppableDestination) {
    const sourceClone = Array.from(source);
    const destClone = Array.from(destination);
    const [removed] = sourceClone.splice(droppableSource.index, 1);

    destClone.splice(droppableDestination.index, 0, removed);

    const result = {};
    result[droppableSource.droppableId] = sourceClone;
    result[droppableDestination.droppableId] = destClone;

    return result;
  }

  onDragEnd(result) {
    const { source, destination } = result;
    console.log(result);

    // dropped outside the list
    if (!destination) {
      return;
    }

    // droppableIds are of the form <table_name>_<key>
    const sInd = source.droppableId;
    const dInd = destination.droppableId;

    // if not the same key-category and not the same field name, drop is not allowed
    if (
      sInd.split("_")[1] !== dInd.split("_")[1] &&
      this.state.cats[sInd.split("_")[1]] !==
        this.state.cats[dInd.split("_")[1]]
    ) {
      return;
    }

    // if dropped in the same place, we reorder the items
    if (sInd === dInd) {
      const items = this.reorder(
        this.state[sInd.split("_")[0]][sInd.split("_")[1]],
        source.index,
        destination.index
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[sInd.split("_")[0]][sInd.split("_")[1]] = items;
      this.setState(newState);
    }
    // if dropped in a different place, move
    else {
      const result = this.move(
        this.state[sInd.split("_")[0]][sInd.split("_")[1]],
        this.state[dInd.split("_")[0]][dInd.split("_")[1]],
        source,
        destination
      );
      const newState = JSON.parse(JSON.stringify(this.state));
      newState[sInd.split("_")[0]][sInd.split("_")[1]] = result[sInd];
      newState[dInd.split("_")[0]][dInd.split("_")[1]] = result[dInd];

      this.setState(newState);
    }
  }

  render() {
    console.log("We have re-rendered:");
    return (
      <Container>
        <DragDropContext onDragEnd={this.onDragEnd}>
          <Row className="justify-content-center">
            <Button
              onClick={() => {
                this.setState({ view: "drag" });
              }}
            >
              Drag and Drop
            </Button>
            <Button
              onClick={() => {
                this.setState({ view: "focusA" });
              }}
            >
              Focus A
            </Button>
            <Button onClick={() => this.setState({ view: "focusB" })}>
              Focus B
            </Button>
            <Button onClick={() => this.setState({ view: "focusC" })}>
              Focus C
            </Button>
            <h1>{this.state.title}</h1>
          </Row>
          <Row show={this.state.view === "drag"}>
            <View
              type={this.state.view}
              original={this.state.original}
              A={this.state.A}
              B={this.state.B}
              C={this.state.C}
              oh={this.state.oh}
              ah={this.state.ah}
              bh={this.state.bh}
              ch={this.state.ch}
              handleInputChange={this.handleInputChange}
            />
          </Row>
        </DragDropContext>
        <Button onClick={this.addEventListeners}>test</Button>
        <Button onClick={this.submit}>Submit</Button>
      </Container>
    );
  }
}

export default Tables;
