import React, { Component } from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import EditIcon from "@material-ui/icons/Edit";
import { getListStyle, getItemStyle } from "./Colours";
import { IconButton, TextareaAutosize } from "@material-ui/core";
import HypothesisModal from "./HypothesisModal";
import KeyModal from "./KeyModal";
import Hypothesis from "./Hypothesis";

// for dark mode
const bgcolors_dict = {
  original: "#DEB2F4",
  A: "#A4CDF0",
  B: "#A2DCE2",
  C: "#F9D3BC",
  trash: "#ccc5b4",
  add: "#ccc5b4",
};

// for light mode
// const bgcolors_dict = {
//   original: "#ca76f5",
//   A: "#5ca1db",
//   B: "#56d3e3",
//   C: "#ffae7d",
//   trash: "#ccc5b4",
//   add: "#ccc5b4",
// };
const hypotheses_dark_colour = "#92d5e6";
const hypotheses_light_colour = "#e1edf0";
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.getDefaultState = this.getDefaultState.bind(this);
    this.handleStateChange = this.handleStateChange.bind(this);
    this.state = this.getDefaultState();
  }

  getDefaultState() {
    let a = new Array(9).fill(0);
    let b = new Array(9).fill(0);
    let c = new Array(9).fill(0);

    let a_rows = new Array(Object.keys(this.props.A).length).fill(0);
    let b_rows = new Array(Object.keys(this.props.B).length).fill(0);
    let c_rows = new Array(Object.keys(this.props.C).length).fill(0);

    let state_var = {
      A: a,
      B: b,
      C: c,
      A_keys: a_rows,
      B_keys: b_rows,
      C_keys: c_rows,
    };
    return state_var;
  }

  tableRows(
    pairs,
    type,
    name = "draggable",
    editable = true,
    checkboxes = false,
    hypo
  ) {
    return (
      <>
        {" "}
        <Container>
          {Object.keys(pairs).map((key, key_idx) => {
            var checkbox = key;
            // console.log("$$$$$$$$$$$$$$");
            // console.log(this.props);
            // console.log(type, hypo);
            if (checkboxes === true) {
              checkbox = (
                <Form>
                  <Form.Check
                    custom
                    type={"checkbox"}
                    defaultChecked={
                      this.props[type.toLowerCase() + "h"][hypo][2].includes(
                        key
                      )
                        ? true
                        : false
                    }
                    id={type + "_check_" + key}
                    label={key}
                    className="checkbox label"
                    onChange={() => {
                      this.props.handleRowChange(type, key, hypo);
                    }}
                  />
                </Form>
              );
            }

            return (
              <Container style={{ marginBottom: "20px" }}>
                <Droppable droppableId={type + "_" + key}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      style={getListStyle(snapshot.isDraggingOver, type)}
                      {...provided.droppableProps}
                    >
                      {!checkboxes && (
                        <div
                          style={{
                            display: "inline-block",
                            whiteSpace: "nowrap",
                            width: "100%",
                          }}
                        >
                          {" "}
                          <center>
                            <strong>{checkbox}</strong>{" "}
                            <IconButton
                              size="small"
                              style={{ marginLeft: "auto" }}
                              onClick={() => {
                                var newState = this.getDefaultState();
                                newState[type + "_keys"][key_idx] = 1;
                                this.setState(newState);
                              }}
                            >
                              {type !== "original" && (
                                <EditIcon
                                  className="float-right"
                                  style={{
                                    width: "15px",
                                    height: "15px",
                                    opacity: "0.5",
                                  }}
                                />
                              )}
                            </IconButton>
                            {type !== "original" && (
                              <KeyModal
                                show={this.state[type + "_keys"][key_idx]}
                                onHide={() =>
                                  this.setState(this.getDefaultState())
                                }
                                onDelete={(type, key) => {
                                  this.props.deleteSection(type, key);
                                }}
                                onEdit={(new_key) => {
                                  this.props.editSectionName(
                                    type,
                                    key,
                                    new_key
                                  );
                                }}
                                section_name={key}
                                type={type}
                              />
                            )}
                          </center>
                        </div>
                      )}
                      {checkboxes && (
                        <div
                          style={{
                            backgroundColor: bgcolors_dict[type],
                            marginLeft: "20px",
                          }}
                        >
                          <strong>{checkbox}</strong>
                        </div>
                      )}
                      {pairs[key].map((value, id) => {
                        // console.log("value is ^^^^^");
                        // console.log(value);
                        return (
                          <Draggable
                            draggableId={type + "_" + key + "_" + id}
                            index={id}
                            className="draggable"
                          >
                            {(provided, snapshot) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                style={getItemStyle(
                                  snapshot.isDragging,
                                  provided.draggableProps.style
                                )}
                              >
                                <Row>
                                  <TextareaAutosize
                                    style={{
                                      marginLeft: "20px",
                                      background: "transparent",
                                      resize: "none",
                                      backgroundColor: "rgba(0, 0, 0, 0)",
                                      borderColor: "rgba(0, 0, 0, 0)",
                                    }}
                                    name={name}
                                    id={"item_" + type + "_" + key + "_" + id}
                                    value={
                                      type === "original"
                                        ? value
                                        : Object.keys(value)[0]
                                    }
                                    onChange={(e) =>
                                      this.props.handleChange(
                                        e,
                                        "item_" + type + "_" + key + "_" + id
                                      )
                                    }
                                  />
                                </Row>
                              </div>
                            )}
                          </Draggable>
                        );
                      })}
                    </div>
                  )}
                </Droppable>
              </Container>
            );
          })}
        </Container>
        {type !== "original" && !checkboxes && (
          <Container>
            {" "}
            <Row>
              <div className="float-right" style={{ width: "100%" }}>
                <Button
                  onClick={() => {
                    var lastIdx = this.props.addSection(
                      type,
                      "New Section Name"
                    );
                    var newState = this.getDefaultState();
                    newState[type + "_keys"][lastIdx] = 1;
                    this.setState(newState);
                  }}
                  className="text-white ml-auto float-right"
                  style={{
                    alignContent: "right",
                    fontSize: "15px",
                    marginRight: "-16px",
                    marginTop: "-15px",
                  }}
                >
                  Add section
                  <AddIcon />
                </Button>
              </div>
            </Row>
          </Container>
        )}
      </>
    );
  }

  handleStateChange(newState) {
    this.setState(newState);
  }

  hypothesesDisplay(hypo, hypo_info, type, idx) {
    return (
      <Hypothesis
        hypo={hypo}
        hypo_info={hypo_info}
        type={type}
        idx={idx}
        getDefaultState={() => this.getDefaultState()}
        handleHypothesisChange={(e, type, hypo) =>
          this.props.handleHypothesisChange(e, type, hypo)
        }
        handleLabelChange={(e, type, idx) =>
          this.props.handleLabelChange(e, type, idx)
        }
        handleStrategyChange={this.props.handleStrategyChange}
        info={this.props[type.toLowerCase() + "h"]}
        rows={this.props[type]}
        tableRows={this.tableRows}
        handleStateChange={this.handleStateChange}
      />
    );
    // var stylecolor =
    //   idx % 2
    //     ? { backgroundColor: hypotheses_light_colour }
    //     : { backgroundColor: hypotheses_dark_colour };

    // var showModal = <></>;
    // // console.log(type, hypo_info, type, idx);
    // if (type !== "original") {
    //   // console.log("~~~~~~~~~~~~~~~");
    //   // console.log(this.props[type.toLowerCase() + "h"][hypo]);
    //   // console.log(hypo);
    //   // console.log("show modal is ");
    //   // console.log(type == "original" ? false : this.state[type][idx]);
    //   showModal = (
    //     <>
    //       <IconButton
    //         size="small"
    //         style={{ marginRight: "-15px" }}
    //         onClick={() => {
    //           if (type == "original") return;
    //           var newState = this.getDefaultState();
    //           newState[type][idx] = 1;
    //           this.setState(newState);
    //           // console.log("^^^^^^^^^we changed the state!");
    //           // console.log(newState[type][idx]);
    //         }}
    //       >
    //         <AddIcon />
    //       </IconButton>
    //       <HypothesisModal
    //         show={type == "original" ? false : this.state[type][idx]}
    //         onHide={() => this.setState(this.getDefaultState())}
    //         hypo={hypo}
    //         idx={idx}
    //         type={type}
    //         table={this.tableRows(
    //           this.props[type],
    //           type,
    //           "non-draggable",
    //           false,
    //           true,
    //           hypo
    //         )}
    //         strategies={this.props[type.toLowerCase() + "h"][hypo][1]}
    //         rows={this.props[type.toLowerCase() + "h"][hypo][2]}
    //         handleStrategyChange={this.props.handleStrategyChange}
    //       />
    //     </>
    //   );
    // }
    // return (
    //   <Row className="align-items-center ml-2" style={stylecolor}>
    //     <div className="input-icon-wrap">
    //       <Col
    //         className="align-items-center"
    //         style={{ height: "100%", width: "30px", marginLeft: "7px" }}
    //       >
    //         <Row style={{ paddingTop: "20px" }}>
    //           {type == "original" && (
    //             <select
    //               defaultValue={hypo_info}
    //               className="disabled-dropdown-arrow text-align-center pl-2"
    //               id={"label_" + type + idx}
    //               style={{
    //                 border: "0px",
    //                 backgroundColor: "white",
    //                 width: "40px",
    //               }}
    //             >
    //               <option value={hypo_info} className="text-align-center pl-2">
    //                 {hypo_info}
    //               </option>
    //             </select>
    //           )}
    //           {type !== "original" && (
    //             <select
    //               style={{ border: "0px", backgroundColor: "white" }}
    //               defaultValue={this.props[type.toLowerCase() + "h"][hypo][0]}
    //               id={"label_" + type + idx}
    //               onChange={(e) => this.props.handleLabelChange(e, type, idx)}
    //             >
    //               <option value="E">E</option>
    //               <option value="N">N</option>
    //               <option value="C">C</option>
    //             </select>
    //           )}
    //         </Row>
    //         <Row style={{ paddingTop: "7px" }}>{showModal}</Row>
    //       </Col>
    //       <Col>
    //         <TextareaAutosize
    //           className="input-with-icon"
    //           id={"hypothesis_" + type + idx}
    //           type="textarea"
    //           defaultValue={hypo}
    //           style={{
    //             resize: "none",
    //             backgroundColor: "rgba(0, 0, 0, 0)",
    //             borderColor: "rgba(0, 0, 0, 0)",
    //           }}
    //           onChange={(e) => this.props.handleHypothesisChange(e, type, hypo)}
    //           contentEditable
    //         />
    //       </Col>
    //     </div>
    //   </Row>
    // );
  }

  showView(type) {
    const ohy = Object.keys(this.props.oh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.oh[hypo], "original", idx);
    });
    var ah_items = Object.keys(this.props.ah).map((key) => [
      key,
      this.props.ah[key],
    ]);
    ah_items.sort((first, second) => second[1][3] - first[1][3]);
    // console.log(ah_items);
    const ahy = ah_items.map((hypo, idx) => {
      // console.log(hypo);
      return this.hypothesesDisplay(hypo[0], hypo[1], "A", idx);
    });
    var bh_items = Object.keys(this.props.bh).map((key) => [
      key,
      this.props.bh[key],
    ]);
    bh_items.sort((first, second) => second[1][3] - first[1][3]);
    const bhy = bh_items.map((hypo, idx) => {
      return this.hypothesesDisplay(hypo[0], hypo[1], "B", idx);
    });
    var ch_items = Object.keys(this.props.ch).map((key) => [
      key,
      this.props.ch[key],
    ]);
    ch_items.sort((first, second) => second[1][3] - first[1][3]);
    const chy = ch_items.map((hypo, idx) => {
      return this.hypothesesDisplay(hypo[0], hypo[1], "C", idx);
    });
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Original Table</h5>
              </Row>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table A</h5>
              </Row>
              {this.tableRows(this.props.A, "A")}
            </Col>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table B</h5>
              </Row>
              {this.tableRows(this.props.B, "B")}
            </Col>
            <Col md={3} style={{ marginBottom: "15px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Table C</h5>
              </Row>
              {this.tableRows(this.props.C, "C")}
            </Col>
          </Row>
          <Row style={{ marginLeft: "10px" }}>
            <Col md={3} style={{ width: "100%", paddingRight: "10px" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h6>Original Hypotheses</h6>
              </Row>
              {ohy}
            </Col>
            <Col md={3} style={{ width: "100%" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h6>Table A Hypotheses</h6>
              </Row>
              {ahy}
            </Col>
            <Col md={3} style={{ width: "100%" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h6>Table B Hypotheses</h6>
              </Row>
              {bhy}
            </Col>
            <Col md={3} style={{ width: "100%" }}>
              <Row className="justify-content-center mt-3 white-text">
                <h6>Table C Hypotheses</h6>
              </Row>
              {chy}
            </Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Row className="mt-3">
            <Col md={3}>
              <Row className="justify-content-center mt-3 white-text">
                <h5>Original Table</h5>
              </Row>
