import React, { Component } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Button } from "react-bootstrap";

class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
  }
  tableRows(pairs, type) {
    return Object.keys(pairs).map((key) => {
      return (
        <Container>
          <Row>
            <Col>
              <strong>{key}</strong>
            </Col>
            <Col>
              <Droppable droppableId={type + "_" + key}>
                {(provided, snapshot) => (
                  <div ref={provided.innerRef} {...provided.droppableProps}>
                    {pairs[key].map((value, id) => {
                      return (
                        <Draggable
                          draggableId={type + "_" + key + "_" + id}
                          index={id}
                        >
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                            >
                              <Row>{value}</Row>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                  </div>
                )}
              </Droppable>
            </Col>
          </Row>
        </Container>
      );
    });
  }
  hypotheses(pairs, type) {
    return Object.keys(pairs).map((hypo, idx) => {
      return (
        <input
          name={type + idx}
          type="textarea"
          value={hypo}
          onChange={this.handleInputChange}
        ></input>
      );
    });
  }
  showView(type) {
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3}>{this.tableRows(this.props.original, "original")}</Col>
            <Col md={3}>{this.tableRows(this.props.A, "A")}</Col>
            <Col md={3}>{this.tableRows(this.props.B, "B")}</Col>
            <Col md={3}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={3}>{this.hypotheses(this.props.oh, "original")}</Col>
            <Col md={3}>{this.hypotheses(this.props.oa, "A")}</Col>
            <Col md={3}>{this.hypotheses(this.props.ob, "B")}</Col>
            <Col md={3}>{this.hypotheses(this.props.oc, "C")}</Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Col md={6}>{this.tableRows(this.props.original, "original")}</Col>
          <Col md={6}>{this.tableRows(this.props.A, "A")}</Col>
        </>
      );
    } else if (type === "focusB") {
      return (
        <>
          <Col md={6}>{this.tableRows(this.props.original, "original")}</Col>
          <Col md={6}>{this.tableRows(this.props.B, "B")}</Col>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Col md={6}>{this.tableRows(this.props.original, "original")}</Col>
          <Col md={6}>{this.tableRows(this.props.C, "C")}</Col>
        </>
      );
    }
  }

  render() {
    return <>{this.showView(this.props.type)}</>;
  }
}

export default View;
