import React, { Component } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import {Container, Row, Col} from 'react-bootstrap';

const axios = require('axios'); 
axios.post('/user', {
firstName: 'Fred',
lastName: 'Flintstone'
})
.then(function (response) {
console.log(response);
})
.catch(function (error) {
console.log(error);
}); 

class Tables extends Component {
    constructor(props) {
        super(props);
        this.onDragEnd = this.onDragEnd.bind(this)
        this.state = {title: '', original: '', A: '', B: '', C: ''}
    }

    componentDidMount() {
        this.getTables();
    }

    getTables() {
        fetch(`/api/table?id=${this.props.tableid}`).then(res => res.json()).then(data => {this.setState({title: data.title, 'original': data.original, 'A': data['A'], 'B': data.B, 'C': data.C})})
    }

    submit() {
        fetch(`/api/table?id=${this.props.tableid}`).then(res => res.json()).then(data => {this.setState({title: data.title, 'original': data.original, 'A': data['A'], 'B': data.B, 'C': data.C})})
    }

    tableRows(pairs, type) {
        return(Object.keys(pairs).map(key => {return(<Container><Row><Col><strong>{key}</strong></Col><Col>
        <Droppable droppableId={type + '_' + key}>
            {(provided, snapshot) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                    {pairs[key].map((value, id) => {
                        return(
                            <Draggable draggableId={type + '_' + key + '_' + id} index={id}>
                                {(provided, snapshot) => (
                                    <div ref={provided.innerRef} {...provided.draggableProps}
                                    {...provided.dragHandleProps}><Row>{value}</Row></div>
                                )}
                            </Draggable>
                        )
                    })}
                </div>
            )}
        </Droppable>
        </Col></Row></Container>)}))
    }

    reorder(list, startIndex, endIndex) {
        console.log(list)
        const result = Array.from(list);
        const [removed] = result.splice(startIndex, 1);
        result.splice(endIndex, 0, removed);


        return result;
      };

      /**
       * Moves an item from one list to another list.
       */
    move(source, destination, droppableSource, droppableDestination) {
        const sourceClone = Array.from(source);
        const destClone = Array.from(destination);
        const [removed] = sourceClone.splice(droppableSource.index, 1);

        destClone.splice(droppableDestination.index, 0, removed);

        const result = {};
        result[droppableSource.droppableId] = sourceClone;
        result[droppableDestination.droppableId] = destClone;

        return result;
      };

    onDragEnd(result) {
        console.log(result)
        const { source, destination } = result;

        // dropped outside the list
        if (!destination) {
          return;
        }
        const sInd = source.droppableId;
        const dInd = destination.droppableId;

        console.log(this.state[sInd.split("_")[0]]);
        console.log(this.state[sInd.split("_")[0]][sInd.split("_")[1]])
        if (sInd === dInd) {
          const items = this.reorder(this.state[sInd.split("_")[0]][sInd.split("_")[1]], source.index, destination.index);
          const newState = JSON.parse(JSON.stringify(this.state)) ;
          newState[sInd.split("_")[0]][sInd.split("_")[1]] = items;
          this.setState(newState);
        } else {
          const result = this.move(this.state[sInd.split("_")[0]][sInd.split("_")[1]], this.state[dInd.split("_")[0]][dInd.split("_")[1]], source, destination);
          const newState = JSON.parse(JSON.stringify(this.state)) ;
          newState[sInd.split("_")[0]][sInd.split("_")[1]] = result[sInd];
          newState[dInd.split("_")[0]][dInd.split("_")[1]] = result[dInd];

          this.setState(newState);
        }
      }

    render() {
        return(
            <Container >
                <DragDropContext onDragEnd={this.onDragEnd}>
                <Row className="justify-content-center">
                    <h1>{this.state.title}</h1>
                </Row>
                <Row>
                <Col md={3}>
                {this.tableRows(this.state.original, 'original')}
                </Col>
                <Col md={3}>
                {this.tableRows(this.state.A, 'A')}
                </Col>
                <Col md={3}>
                {this.tableRows(this.state.B, 'B')}
                </Col>
                <Col md={3}>
                {this.tableRows(this.state.C, 'C')}
                </Col>
                </Row>
                </DragDropContext>
                <Button onClick={this.submit()}>Submit</Button>
            </Container>
        );
    }
}

export default Tables;
