import React, { Component } from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import { Container, Row, Col, Form } from "react-bootstrap";
import AddIcon from "@material-ui/icons/Add";
import { IconButton, TextareaAutosize } from "@material-ui/core";
import ShowModal from "./Modal";

const grid = 5;
const bgcolor = "#92d5e6";
const rowcolor = "#f5eef8";
class View extends Component {
  constructor(props) {
    super(props);
    this.tableRows = this.tableRows.bind(this);
    this.showView = this.showView.bind(this);
    this.hypothesesDisplay = this.hypothesesDisplay.bind(this);
    this.state = this.getDefaultState();
  }

  getDefaultState() {
    let a = new Array(9).fill(0);
    let b = new Array(9).fill(0);
    let c = new Array(9).fill(0);
    let state_var = { A: a, B: b, C: c };
    return state_var;
  }
  getListStyle(isDraggingOver) {
    return {
      background: isDraggingOver ? "#BADDE0" : bgcolor,
      padding: grid,
      minWidth: "250px",
      minHeight: "50px",
    };
  }
  getItemStyle(isDragging, draggableStyle) {
    return {
      // some basic styles to make the items look a bit nicer
      userSelect: "none",
      padding: grid * 2,
      margin: `0 0 ${grid}px 0`,

      // change background colour if dragging
      background: isDragging ? "lightgreen" : rowcolor,

      // styles we need to apply on draggables
      ...draggableStyle,
    };
  }
  tableRows(
    pairs,
    type,
    name = "draggable",
    editable = true,
    checkboxes = false,
    hypo
  ) {
    return Object.keys(pairs).map((key) => {
      var checkbox = key;
      // console.log("$$$$$$$$$$$$$$");
      // console.log(this.props);
      // console.log(type, hypo);
      if (checkboxes === true) {
        checkbox = (
          <Form>
            <Form.Check
              custom
              type={"checkbox"}
              defaultChecked={
                this.props[type.toLowerCase() + "h"][hypo][2].includes(key)
                  ? true
                  : false
              }
              id={type + "_check_" + key}
              label={key}
              onChange={() => {
                this.props.handleRowChange(type, key, hypo);
              }}
            />
          </Form>
        );
      }

      return (
        <Container>
          <Droppable droppableId={type + "_" + key}>
            {(provided, snapshot) => (
              <div
                ref={provided.innerRef}
                style={this.getListStyle(snapshot.isDraggingOver)}
                {...provided.droppableProps}
              >
                <center
                  style={{
                    backgroundColor: bgcolor,
                  }}
                >
                  <strong>{checkbox}</strong>
                </center>
                {pairs[key].map((value, id) => {
                  // console.log("value is ^^^^^");
                  // console.log(value);
                  return (
                    <Draggable
                      draggableId={type + "_" + key + "_" + id}
                      index={id}
                    >
                      {(provided, snapshot) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          style={this.getItemStyle(
                            snapshot.isDragging,
                            provided.draggableProps.style
                          )}
                        >
                          <Row>
                            <TextareaAutosize
                              style={{
                                margin: "5px",
                                background: "transparent",
                              }}
                              name={name}
                              id={"item_" + type + "_" + key + "_" + id}
                              value={
                                type === "original"
                                  ? value
                                  : Object.keys(value)[0]
                              }
                              style={{
                                resize: "none",
                                backgroundColor: "rgba(0, 0, 0, 0)",
                                borderColor: "rgba(0, 0, 0, 0)",
                              }}
                              onChange={(e) =>
                                this.props.handleChange(
                                  e,
                                  "item_" + type + "_" + key + "_" + id
                                )
                              }
                            />
                          </Row>
                        </div>
                      )}
                    </Draggable>
                  );
                })}
              </div>
            )}
          </Droppable>
        </Container>
      );
    });
  }

  hypothesesDisplay(hypo, hypo_info, type, idx) {
    var stylecolor =
      idx % 2
        ? { backgroundColor: "#E1ECF4" }
        : { backgroundColor: "light green" };

    var showModal = <></>;
    // console.log(type, hypo_info, type, idx);
    if (type !== "original") {
      // console.log("~~~~~~~~~~~~~~~");
      // console.log(this.props[type.toLowerCase() + "h"][hypo]);
      // console.log(hypo);
      // console.log("show modal is ");
      // console.log(type == "original" ? false : this.state[type][idx]);
      showModal = (
        <>
          <IconButton
            onClick={() => {
              if (type == "original") return;
              var newState = this.getDefaultState();
              newState[type][idx] = 1;
              this.setState(newState);
              // console.log("^^^^^^^^^we changed the state!");
              // console.log(newState[type][idx]);
            }}
          >
            <AddIcon />
          </IconButton>
          <ShowModal
            show={type == "original" ? false : this.state[type][idx]}
            onHide={() => this.setState(this.getDefaultState())}
            hypo={hypo}
            idx={idx}
            type={type}
            table={this.tableRows(
              this.props[type],
              type,
              "non-draggable",
              false,
              true,
              hypo
            )}
            strategies={this.props[type.toLowerCase() + "h"][hypo][1]}
            rows={this.props[type.toLowerCase() + "h"][hypo][2]}
            handleStrategyChange={this.props.handleStrategyChange}
          />
        </>
      );
    }
    return (
      <Row style={stylecolor}>
        <Col>
          <div className="float-right" style={{ width: "10px" }}>
            <Row>{showModal}</Row>
            <Row>
              <select
                defaultValue={
                  type == "original"
                    ? hypo_info
                    : this.props[type.toLowerCase() + "h"][hypo][0]
                }
                id={"label_" + type + idx}
                onChange={(e) => this.props.handleLabelChange(e, type, idx)}
              >
                <option value="E">E</option>
                <option value="N">N</option>
                <option value="C">C</option>
              </select>
            </Row>
          </div>
        </Col>
        <Col>
          <div style={{ width: "auto", overflow: "hidden" }}>
            <TextareaAutosize
              id={"hypothesis_" + type + idx}
              type="textarea"
              defaultValue={hypo}
              style={{
                resize: "none",
                backgroundColor: "rgba(0, 0, 0, 0)",
                borderColor: "rgba(0, 0, 0, 0)",
              }}
              onChange={(e) => this.props.handleHypothesisChange(e, type, hypo)}
              contentEditable
            />
          </div>
        </Col>
      </Row>
    );
  }
  trash() {
    return (
      <Droppable droppableId={"trash"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver)}
            {...provided.droppableProps}
          >
            Drag and drop a value here to delete it
            {() => {
              return <></>;
            }}
          </div>
        )}
      </Droppable>
    );
  }
  addCard() {
    // console.log("ADD");
    return (
      <Droppable droppableId={"add"}>
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            style={this.getListStyle(snapshot.isDraggingOver)}
            {...provided.droppableProps}
          >
            {[1].map((value) => {
              return (
                <Draggable draggableId={"new"}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={this.getItemStyle(
                        snapshot.isDragging,
                        provided.draggableProps.style
                      )}
                    >
                      <Row>
                        <input
                          style={{
                            margin: "5px",
                            background: "transparent",
                            width: "100%",
                            border: "none",
                          }}
                          name="draggable"
                          id={"NEW"}
                          defaultValue={
                            "Add a new value here! (write text, then drag and drop)"
                          }
                        />
                      </Row>
                    </div>
                  )}
                </Draggable>
              );
            })}
          </div>
        )}
      </Droppable>
    );
  }
  showView(type) {
    const ohy = Object.keys(this.props.oh).map((hypo, idx) => {
      return this.hypothesesDisplay(hypo, this.props.oh[hypo], "original", idx);
    });
    var ah_items = Object.keys(this.props.ah).map((key) => [
      key,
      this.props.ah[key],
    ]);
    ah_items.sort((first, second) => second[1][3] - first[1][3]);
    // console.log(ah_items);
    const ahy = ah_items.map((hypo, idx) => {
      // console.log(hypo);
      return this.hypothesesDisplay(hypo[0], hypo[1], "A", idx);
    });
    var bh_items = Object.keys(this.props.bh).map((key) => [
      key,
      this.props.bh[key],
    ]);
    bh_items.sort((first, second) => second[1][3] - first[1][3]);
    const bhy = bh_items.map((hypo, idx) => {
      return this.hypothesesDisplay(hypo[0], hypo[1], "B", idx);
    });
    var ch_items = Object.keys(this.props.ch).map((key) => [
      key,
      this.props.ch[key],
    ]);
    ch_items.sort((first, second) => second[1][3] - first[1][3]);
    const chy = ch_items.map((hypo, idx) => {
      return this.hypothesesDisplay(hypo[0], hypo[1], "C", idx);
    });
    if (type === "drag") {
      return (
        <>
          <Row>
            <Col md={3}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={3}>{this.tableRows(this.props.A, "A")}</Col>
            <Col md={3}>{this.tableRows(this.props.B, "B")}</Col>
            <Col md={3}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={3}>{ohy}</Col>
            <Col md={3}>{ahy}</Col>
            <Col md={3}>{bhy}</Col>
            <Col md={3}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusA") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.A, "A")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{ahy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusB") {
      return (
        <>
          <Row>
            {" "}
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.B, "B")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{bhy}</Col>
          </Row>
        </>
      );
    } else if (type === "focusC") {
      return (
        <>
          <Row>
            <Col md={6}>
              {this.tableRows(
                this.props.original,
                "original",
                "non-draggable",
                false
              )}
            </Col>
            <Col md={6}>{this.tableRows(this.props.C, "C")}</Col>
          </Row>
          <Row>
            <Col md={6}>{ohy}</Col>
            <Col md={6}>{chy}</Col>
          </Row>
        </>
      );
    } else if (type == "trash") {
      return <Col md={6}>{this.trash()}</Col>;
    } else if (type == "add") {
      return <Col md={6}>{this.addCard()}</Col>;
    }
  }

  render() {
    return <>{this.showView(this.props.type)}</>;
  }
}

export default View;
